import { NextResponse } from "next/server";
import { extractVideoId } from "@/lib/utils";

export async function POST(req: Request) {
    try {
        const { query } = await req.json();

        if (!query) {
            return NextResponse.json(
                { error: "Please enter a channel URL or handle" },
                { status: 400 }
            );
        }

        const apiKey = process.env.YOUTUBE_API_KEY;

        if (!apiKey) {
            return NextResponse.json(
                { error: "Server configuration error: API key missing" },
                { status: 500 }
            );
        }

        // 1. Try to find by handle (if starts with @)
        let searchPart = "snippet,id";
        let searchType = "channel";
        let apiUrl = `https://www.googleapis.com/youtube/v3/search?part=${searchPart}&q=${encodeURIComponent(query)}&type=${searchType}&key=${apiKey}`;

        // If it looks like a channel ID (UC...) we can use channels endpoint directly?
        // Actually search is safer as it handles handles, names, etc.
        // But for exact channel ID lookup, channels endpoint is better.
        // Let's stick to search for flexibility, as it handles handles well.

        // If it's a full URL, try to extract the useful part
        let cleanQuery = query;
        try {
            if (query.includes('youtube.com/')) {
                const urlObj = new URL(query);
                const pathSegments = urlObj.pathname.split('/').filter(Boolean);
                if (pathSegments[0] === 'channel') {
                    // specific channel ID
                    apiUrl = `https://www.googleapis.com/youtube/v3/channels?part=${searchPart}&id=${pathSegments[1]}&key=${apiKey}`;
                } else if (pathSegments[0] === 'c' || pathSegments[0] === 'user' || pathSegments[0].startsWith('@')) {
                    // handle or custom url -> use search
                    cleanQuery = pathSegments[pathSegments.length - 1]; // fallback logic
                    apiUrl = `https://www.googleapis.com/youtube/v3/search?part=${searchPart}&q=${encodeURIComponent(query)}&type=${searchType}&key=${apiKey}`;
                }
            }
        } catch (e) {
            // ignore invalid url and treat as string
        }

        // 2. Check if it's a video URL (e.g. youtu.be/VIDEO_ID)
        const videoId = extractVideoId(query);
        if (videoId) {
            const videoResponse = await fetch(
                `https://www.googleapis.com/youtube/v3/videos?part=snippet&id=${videoId}&key=${apiKey}`
            );
            const videoData = await videoResponse.json();

            if (videoData.items && videoData.items.length > 0) {
                // Found the video, use its channel ID
                const channelId = videoData.items[0].snippet.channelId;
                apiUrl = `https://www.googleapis.com/youtube/v3/channels?part=${searchPart}&id=${channelId}&key=${apiKey}`;
            }
        }

        const response = await fetch(apiUrl);
        const data = await response.json();

        if (!response.ok) {
            return NextResponse.json(
                { error: data.error?.message || "YouTube API error" },
                { status: response.status }
            );
        }

        if (!data.items || data.items.length === 0) {
            return NextResponse.json(
                { error: "No channel found" },
                { status: 404 }
            );
        }

        const item = data.items[0];

        // If we used search, we only get basic info. We might want to fetch full channel details with the ID we found
        // to get proper statistics or high res snippet.
        const channelId = item.id?.channelId || item.id; // search returns id object, channels returns string id (actually inside id property for both usually? let's check) -- search: id: { kind: "youtube#channel", channelId: "UC..." }, channels: id: "UC..."

        const finalId = typeof channelId === 'string' ? channelId : channelId.channelId;

        if (!finalId) {
            return NextResponse.json(
                { error: "Could not resolve channel ID" },
                { status: 404 }
            );
        }

        const detailsResponse = await fetch(
            `https://www.googleapis.com/youtube/v3/channels?part=snippet,statistics,contentDetails&id=${finalId}&key=${apiKey}`
        );
        const detailsData = await detailsResponse.json();

        if (!detailsData.items || detailsData.items.length === 0) {
            return NextResponse.json(
                { error: "Channel details not found" },
                { status: 404 }
            );
        }

        const channelDetails = detailsData.items[0];

        return NextResponse.json({
            id: channelDetails.id,
            title: channelDetails.snippet.title,
            description: channelDetails.snippet.description,
            customUrl: channelDetails.snippet.customUrl,
            publishedAt: channelDetails.snippet.publishedAt,
            thumbnails: channelDetails.snippet.thumbnails,
            statistics: channelDetails.statistics,
        });

    } catch (error) {
        console.error("Channel ID API error:", error);
        return NextResponse.json(
            { error: "Internal server error" },
            { status: 500 }
        );
    }
}

import { NextResponse } from "next/server";
import { extractVideoId } from "@/lib/utils";

export async function POST(req: Request) {
    try {
        const { url, includeReplies = false } = await req.json();

        if (!url) {
            return NextResponse.json(
                { error: "Please enter a video URL" },
                { status: 400 }
            );
        }

        const videoId = extractVideoId(url);
        if (!videoId) {
            return NextResponse.json(
                { error: "Invalid YouTube URL" },
                { status: 400 }
            );
        }

        const apiKey = process.env.YOUTUBE_API_KEY;

        if (!apiKey) {
            return NextResponse.json(
                { error: "Server configuration error" },
                { status: 500 }
            );
        }

        let allComments: any[] = [];
        let nextPageToken: string | null = "";
        const maxComments = 2000; // Safety limit
        let fetchedCount = 0;

        // Fetch comments
        while (nextPageToken !== null && fetchedCount < maxComments) {
            const apiUrl: string = `https://www.googleapis.com/youtube/v3/commentThreads?part=snippet&videoId=${videoId}&maxResults=100&order=relevance&key=${apiKey}&pageToken=${nextPageToken || ""}`;

            const response = await fetch(apiUrl);
            const data = await response.json();

            if (data.error) {
                // specific check for disabled comments
                if (data.error.errors?.[0]?.reason === 'commentsDisabled') {
                    return NextResponse.json(
                        { error: "Comments are disabled for this video." },
                        { status: 400 }
                    );
                }
                return NextResponse.json(
                    { error: data.error.message },
                    { status: 400 }
                );
            }

            if (data.items) {
                const comments = data.items.map((item: any) => ({
                    id: item.id,
                    text: item.snippet.topLevelComment.snippet.textDisplay,
                    authorDisplayName: item.snippet.topLevelComment.snippet.authorDisplayName,
                    authorProfileImageUrl: item.snippet.topLevelComment.snippet.authorProfileImageUrl,
                    authorChannelUrl: item.snippet.topLevelComment.snippet.authorChannelUrl,
                    publishedAt: item.snippet.topLevelComment.snippet.publishedAt,
                    likeCount: item.snippet.topLevelComment.snippet.likeCount,
                }));
                allComments = [...allComments, ...comments];
                fetchedCount += comments.length;
            }

            nextPageToken = data.nextPageToken || null;
        }

        return NextResponse.json({
            comments: allComments,
            count: allComments.length,
            hasMore: nextPageToken !== null, // To indicate if we hit the limit
        });

    } catch (error) {
        console.error("Comment Picker API error:", error);
        return NextResponse.json(
            { error: "Internal server error" },
            { status: 500 }
        );
    }
}

import { NextResponse } from "next/server";
import { parseDuration } from "@/lib/utils";

export async function POST(req: Request) {
    try {
        const { query } = await req.json();

        if (!query) {
            return NextResponse.json(
                { error: "Please enter a playlist URL" },
                { status: 400 }
            );
        }

        const apiKey = process.env.YOUTUBE_API_KEY;

        if (!apiKey) {
            return NextResponse.json(
                { error: "Server configuration error" },
                { status: 500 }
            );
        }

        // Extract playlist ID
        let playlistId = query;
        const urlParams = new URL(query).searchParams;
        if (urlParams.has("list")) {
            playlistId = urlParams.get("list")!;
        }

        if (!playlistId) {
            return NextResponse.json(
                { error: "Invalid playlist URL" },
                { status: 400 }
            );
        }

        let totalSeconds = 0;
        let videoCount = 0;
        let nextPageToken: string | null = "";
        const maxVideos = 500; // Safety limit
        let fetchedVideos = 0;

        // Fetch playlist details (title, thumbnail)
        const playlistResponse = await fetch(
            `https://www.googleapis.com/youtube/v3/playlists?part=snippet&id=${playlistId}&key=${apiKey}`
        );
        const playlistData = await playlistResponse.json();

        if (!playlistData.items || playlistData.items.length === 0) {
            return NextResponse.json(
                { error: "Playlist not found or private" },
                { status: 404 }
            );
        }

        const playlistInfo = {
            title: playlistData.items[0].snippet.title,
            thumbnail: playlistData.items[0].snippet.thumbnails?.medium?.url || playlistData.items[0].snippet.thumbnails?.default?.url,
            channelTitle: playlistData.items[0].snippet.channelTitle,
        };

        // Fetch videos
        while (nextPageToken !== null && fetchedVideos < maxVideos) {
            const itemsUrl: string = `https://www.googleapis.com/youtube/v3/playlistItems?part=contentDetails&maxResults=50&playlistId=${playlistId}&key=${apiKey}&pageToken=${nextPageToken || ""}`;
            const itemsResponse = await fetch(itemsUrl);
            const itemsData = await itemsResponse.json();

            if (itemsData.error) {
                return NextResponse.json(
                    { error: itemsData.error.message },
                    { status: 400 }
                );
            }

            const videoIds = itemsData.items.map((item: any) => item.contentDetails.videoId);
            if (videoIds.length === 0) break;

            // Fetch video durations
            const videosUrl = `https://www.googleapis.com/youtube/v3/videos?part=contentDetails&id=${videoIds.join(",")}&key=${apiKey}`;
            const videosResponse = await fetch(videosUrl);
            const videosData = await videosResponse.json();

            if (videosData.items) {
                videosData.items.forEach((video: any) => {
                    totalSeconds += parseDuration(video.contentDetails.duration);
                    videoCount++;
                });
            }

            fetchedVideos += videoIds.length;
            nextPageToken = itemsData.nextPageToken || null;
        }

        return NextResponse.json({
            playlistInfo,
            videoCount,
            totalSeconds,
            averageSeconds: videoCount > 0 ? Math.round(totalSeconds / videoCount) : 0,
        });

    } catch (error) {
        console.error("Playlist API error:", error);
        return NextResponse.json(
            { error: "Internal server error" },
            { status: 500 }
        );
    }
}

import type { Metadata, Viewport } from "next";
import { Inter } from "next/font/google";
import "./globals.css";
import Header from "@/components/layout/Header";
import Footer from "@/components/layout/Footer";
import ScrollToTop from "@/components/ui/ScrollToTop";
import CookieConsent from "@/components/ui/CookieConsent";
import { siteConfig } from "@/config/site";
import { getOrganizationSchema, getWebsiteSchema } from "@/lib/seo";
import AuthProvider from "@/components/providers/AuthProvider";

const inter = Inter({
  variable: "--font-geist-sans",
  subsets: ["latin"],
  display: "swap",
  preload: true,
});

// Viewport configuration for better mobile performance
export const viewport: Viewport = {
  width: "device-width",
  initialScale: 1,
  maximumScale: 5,
  themeColor: [
    { media: "(prefers-color-scheme: light)", color: "#ffffff" },
    { media: "(prefers-color-scheme: dark)", color: "#0a0a0a" },
  ],
};

export const metadata: Metadata = {
  title: {
    default: siteConfig.seo.defaultTitle,
    template: siteConfig.seo.titleTemplate,
  },
  description: siteConfig.seo.defaultDescription,
  keywords: siteConfig.seo.keywords,
  authors: [{ name: siteConfig.name }],
  creator: siteConfig.name,
  publisher: siteConfig.name,
  metadataBase: new URL(siteConfig.url),
  alternates: {
    canonical: siteConfig.url,
  },
  openGraph: {
    type: "website",
    locale: siteConfig.seo.openGraph.locale,
    siteName: siteConfig.seo.openGraph.siteName,
    title: siteConfig.seo.defaultTitle,
    description: siteConfig.seo.defaultDescription,
    url: siteConfig.url,
    images: [
      {
        url: `${siteConfig.url}/og-image.png`,
        width: 1200,
        height: 630,
        alt: siteConfig.name,
      },
    ],
  },
  twitter: {
    card: "summary_large_image",
    title: siteConfig.seo.defaultTitle,
    description: siteConfig.seo.defaultDescription,
    images: [`${siteConfig.url}/og-image.png`],
    creator: "@youtubetools",
  },
  robots: {
    index: true,
    follow: true,
    nocache: false,
    googleBot: {
      index: true,
      follow: true,
      noimageindex: false,
      "max-video-preview": -1,
      "max-image-preview": "large",
      "max-snippet": -1,
    },
  },
  verification: {
    google: "google299d0fa42c6b8fbb",
  },
  category: "technology",
  classification: "YouTube Tools, SEO Tools, Content Creator Tools",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  // Generate JSON-LD structured data
  const organizationSchema = getOrganizationSchema();
  const websiteSchema = getWebsiteSchema();

  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        {/* DNS Prefetch for external resources */}
        <link rel="dns-prefetch" href="https://fonts.googleapis.com" />
        <link rel="dns-prefetch" href="https://i.ytimg.com" />
        <link rel="dns-prefetch" href="https://www.google-analytics.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />

        {/* Favicons */}
        <link rel="icon" href="/favicon.svg" type="image/svg+xml" />
        <link rel="apple-touch-icon" href="/icon.svg" />

        {/* Preload critical resources */}
        <link rel="preload" as="style" href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap" />

        {/* JSON-LD Structured Data for SEO */}
        <script
          type="application/ld+json"
          dangerouslySetInnerHTML={{
            __html: JSON.stringify(organizationSchema),
          }}
        />
        <script
          type="application/ld+json"
          dangerouslySetInnerHTML={{
            __html: JSON.stringify(websiteSchema),
          }}
        />

        {/* Theme initialization script - runs before paint to prevent flash */}
        <script
          dangerouslySetInnerHTML={{
            __html: `
              (function() {
                try {
                  var theme = localStorage.getItem('theme');
                  if (theme === 'dark' || (!theme && window.matchMedia('(prefers-color-scheme: dark)').matches)) {
                    document.documentElement.classList.add('dark');
                  } else {
                    document.documentElement.classList.remove('dark');
                  }
                } catch (e) {}
              })();
            `,
          }}
        />

        {/* Google Analytics */}
        <script async src="https://www.googletagmanager.com/gtag/js?id=G-14MEY3M1CN"></script>
        <script
          dangerouslySetInnerHTML={{
            __html: `
              window.dataLayer = window.dataLayer || [];
              function gtag(){dataLayer.push(arguments);}
              gtag('js', new Date());
              gtag('config', 'G-14MEY3M1CN');
            `,
          }}
        />
      </head>
      <body className={`${inter.variable} antialiased min-h-screen flex flex-col`}>
        <AuthProvider>
          <Header />
          <main className="flex-1">{children}</main>
          <Footer />
          <ScrollToTop />
          <CookieConsent />
        </AuthProvider>
      </body>
    </html>
  );
}

