"use client";

import { useState } from "react";
import { Input } from "@/components/ui/Input";
import Button from "@/components/ui/Button";
import ToolPageLayout from "@/components/tools/ToolPageLayout";
import { FaSearch, FaSpinner, FaTrophy, FaRandom, FaFilter, FaUser, FaQuoteLeft } from "react-icons/fa";
import Image from "next/image";
import confetti from "canvas-confetti";

const faq = [
    {
        question: "Is this tool free?",
        answer: "Yes, this Random Comment Picker is 100% free to use for your giveaways and contests."
    },
    {
        question: "Does it include replies?",
        answer: "Currently, we only pick from top-level comments to ensure fairness, as replies are often discussions."
    },
    {
        question: "How do you filter duplicates?",
        answer: "If you check 'Filter Duplicates', we only count one comment per user. Even if they commented 10 times, they only have 1 chance to win."
    },
    {
        question: "Is it truly random?",
        answer: "Yes, we use a cryptographically secure random number generator to select the winner from the list of qualified comments."
    },
];

const howTo = [
    "Copy the URL of your YouTube video",
    "Paste it into the box below and click 'Load Comments'",
    "Choose your settings (Filter duplicates, specific text)",
    "Click 'Pick Winner' to randomly select a lucky commenter"
];

const seoContent = `Pick a random winner for your YouTube giveaway or contest with our free Comment Picker. 
Fair, transparent, and easy to use. Filter out duplicates and spammers to ensure a legitimate winner every time.`;

interface Comment {
    id: string;
    text: string;
    authorDisplayName: string;
    authorProfileImageUrl: string;
    authorChannelUrl: string;
    publishedAt: string;
    likeCount: number;
}

export default function CommentPicker() {
    const [url, setUrl] = useState("");
    const [loading, setLoading] = useState(false);
    const [fetching, setFetching] = useState(false);
    const [error, setError] = useState("");

    // Data
    const [originalComments, setOriginalComments] = useState<Comment[]>([]);
    const [filteredComments, setFilteredComments] = useState<Comment[]>([]);
    const [winner, setWinner] = useState<Comment | null>(null);

    // Settings
    const [filterDuplicates, setFilterDuplicates] = useState(true);
    const [filterText, setFilterText] = useState("");
    const [excludeUsers, setExcludeUsers] = useState("");

    const handleLoadComments = async () => {
        setError("");
        setWinner(null);
        setOriginalComments([]);
        setFilteredComments([]);

        if (!url.trim()) {
            setError("Please enter a video URL");
            return;
        }

        setFetching(true);

        try {
            const response = await fetch("/api/comment-picker", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ url: url.trim() }),
            });

            const data = await response.json();

            if (!response.ok) {
                setError(data.error || "Failed to load comments");
                return;
            }

            setOriginalComments(data.comments);
            applyFilters(data.comments);
        } catch (err) {
            console.error("Comment load error:", err);
            setError("An unexpected error occurred. Please try again.");
        } finally {
            setFetching(false);
        }
    };

    const applyFilters = (comments: Comment[] = originalComments) => {
        let result = [...comments];

        // 1. Text filter
        if (filterText) {
            const lowerFilter = filterText.toLowerCase();
            result = result.filter(c => c.text.toLowerCase().includes(lowerFilter));
        }

        // 2. Duplicates
        if (filterDuplicates) {
            const seen = new Set();
            result = result.filter(c => {
                const uniqueId = c.authorChannelUrl;
                if (seen.has(uniqueId)) return false;
                seen.add(uniqueId);
                return true;
            });
        }

        // 3. Exclude specific users (comma separated names) - optional basic implementation

        setFilteredComments(result);
        setWinner(null); // Reset winner if filters change
    };

    // Re-apply filters when settings change
    const handleSettingChange = (duplicates: boolean, text: string) => {
        setFilterDuplicates(duplicates);
        setFilterText(text);

        // We need to apply filters to original comments with NEW settings
        // But we can't use state immediately here, so pass args or use effect
        // Using a helper function for clarity
        let result = [...originalComments];
        if (text) {
            const lowerFilter = text.toLowerCase();
            result = result.filter(c => c.text.toLowerCase().includes(lowerFilter));
        }
        if (duplicates) {
            const seen = new Set();
            result = result.filter(c => {
                const uniqueId = c.authorChannelUrl; // Use channel URL as unique user ID
                if (seen.has(uniqueId)) return false;
                seen.add(uniqueId);
                return true;
            });
        }
        setFilteredComments(result);
        setWinner(null);
    };

    const pickWinner = () => {
        if (filteredComments.length === 0) return;

        setLoading(true);
        // Simulate "rolling" effect
        setTimeout(() => {
            const randomIndex = Math.floor(Math.random() * filteredComments.length);
            const luckyWinner = filteredComments[randomIndex];
            setWinner(luckyWinner);
            setLoading(false);

            // Confetti
            confetti({
                particleCount: 150,
                spread: 70,
                origin: { y: 0.6 }
            });
        }, 800);
    };

    return (
        <ToolPageLayout
            title="YouTube Comment Picker"
            description="Pick a random winner for your YouTube giveaway. Filter duplicates and find a fair winner instantly."
            faq={faq}
            howTo={howTo}
            seoContent={seoContent}
        >
            <div className="space-y-8">
                {/* Input Section */}
                <div className="max-w-2xl mx-auto space-y-4">
                    <div className="flex flex-col sm:flex-row gap-4">
                        <div className="flex-1">
                            <Input
                                label="Video URL"
                                placeholder="https://youtube.com/watch?v=..."
                                value={url}
                                onChange={(e) => setUrl(e.target.value)}
                                error={error}
                            />
                        </div>
                        <div className="sm:pt-7">
                            <Button onClick={handleLoadComments} isLoading={fetching} disabled={fetching} className="w-full sm:w-auto">
                                {fetching ? (
                                    <>
                                        <FaSpinner className="mr-2 animate-spin" />
                                        Loading...
                                    </>
                                ) : (
                                    <>
                                        <FaSearch className="mr-2" />
                                        Load Comments
                                    </>
                                )}
                            </Button>
                        </div>
                    </div>

                    {/* Filters - Only show if we have comments or if we want to set them beforehand? Better to show always or after load? Show always for convenience */}
                    <div className="bg-gray-50 dark:bg-gray-900/50 p-4 rounded-xl border border-gray-100 dark:border-gray-800 space-y-4">
                        <h3 className="font-semibold text-gray-900 dark:text-white flex items-center gap-2">
                            <FaFilter className="text-gray-400" /> Contest Rules
                        </h3>

                        <div className="flex flex-col sm:flex-row gap-6">
                            <label className="flex items-center gap-3 cursor-pointer">
                                <input
                                    type="checkbox"
                                    checked={filterDuplicates}
                                    onChange={(e) => handleSettingChange(e.target.checked, filterText)}
                                    className="w-5 h-5 rounded border-gray-300 text-red-600 focus:ring-red-500"
                                />
                                <span className="text-gray-700 dark:text-gray-300">Filter Duplicates</span>
                            </label>

                            <div className="flex-1">
                                <input
                                    type="text"
                                    placeholder="Must contain text (e.g. #giveaway)"
                                    value={filterText}
                                    onChange={(e) => handleSettingChange(filterDuplicates, e.target.value)}
                                    className="w-full text-sm rounded-lg border-gray-300 dark:border-gray-700 dark:bg-gray-800 focus:ring-red-500 focus:border-red-500"
                                />
                            </div>
                        </div>
                    </div>
                </div>

                {/* Status Bar */}
                {originalComments.length > 0 && (
                    <div className="flex flex-wrap items-center justify-between gap-4 bg-blue-50 dark:bg-blue-900/20 p-4 rounded-xl text-blue-800 dark:text-blue-200">
                        <div className="font-medium">
                            Found <span className="font-bold">{originalComments.length}</span> total comments.
                            <span className="mx-2 opacity-50">|</span>
                            <span className="font-bold text-green-600 dark:text-green-400">{filteredComments.length}</span> qualified entries.
                        </div>
                        <Button onClick={pickWinner} isLoading={loading} disabled={loading || filteredComments.length === 0} variant="primary">
                            <FaTrophy className="mr-2" />
                            Pick a Winner
                        </Button>
                    </div>
                )}

                {/* Winner Card */}
                {winner && (
                    <div className="animate-scale-in bg-gradient-to-br from-yellow-50 to-orange-50 dark:from-yellow-900/20 dark:to-orange-900/20 border-2 border-yellow-200 dark:border-yellow-700 rounded-3xl p-8 relative overflow-hidden text-center">
                        <div className="absolute top-0 right-0 p-4 opacity-10">
                            <FaTrophy className="w-32 h-32 text-yellow-600" />
                        </div>

                        <div className="relative z-10">
                            <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-6">ðŸŽ‰ We have a Winner! ðŸŽ‰</h2>

                            <div className="inline-block relative mb-4">
                                <div className="w-24 h-24 rounded-full border-4 border-yellow-400 shadow-xl overflow-hidden relative">
                                    <Image
                                        src={winner.authorProfileImageUrl}
                                        alt={winner.authorDisplayName}
                                        fill
                                        className="object-cover"
                                    />
                                </div>
                                <div className="absolute -bottom-2 -right-2 bg-yellow-400 text-yellow-900 p-2 rounded-full shadow-lg">
                                    <FaTrophy className="w-5 h-5" />
                                </div>
                            </div>

                            <h3 className="text-xl font-bold text-gray-900 dark:text-white mb-1">
                                {winner.authorDisplayName}
                            </h3>
                            <a href={winner.authorChannelUrl} target="_blank" rel="noopener noreferrer" className="text-sm text-gray-500 hover:text-red-500 underline mb-6 inline-block">
                                View Channel
                            </a>

                            <div className="bg-white/80 dark:bg-black/20 rounded-xl p-6 max-w-xl mx-auto backdrop-blur-sm relative">
                                <FaQuoteLeft className="absolute top-4 left-4 text-gray-300 dark:text-gray-600 w-6 h-6" />
                                <p className="text-lg text-gray-700 dark:text-gray-300 italic relative z-10 px-6">
                                    &quot;{winner.text}&quot;
                                </p>
                                <div className="mt-4 flex items-center justify-center gap-4 text-sm text-gray-500">
                                    <span>â¤ï¸ {winner.likeCount} likes</span>
                                    <span>ðŸ“… {new Date(winner.publishedAt).toLocaleDateString()}</span>
                                </div>
                            </div>

                            <div className="mt-8">
                                <a
                                    href={`https://www.youtube.com/watch?v=${extractVideoId(url)}&lc=${winner.id}`}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    <Button variant="outline">
                                        View Comment on YouTube
                                    </Button>
                                </a>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </ToolPageLayout>
    );
}

// Helper to extract video ID (reused but local for safety in rendering link)
function extractVideoId(url: string) {
    const match = url.match(/(?:youtube\.com\/watch\?v=|youtu\.be\/|youtube\.com\/embed\/|youtube\.com\/v\/)([a-zA-Z0-9_-]{11})/);
    return match ? match[1] : null;
}

"use client";

import { useState } from "react";
import { Input } from "@/components/ui/Input";
import Button from "@/components/ui/Button";
import ToolPageLayout from "@/components/tools/ToolPageLayout";
import { FaSearch, FaSpinner, FaListUl, FaPlayCircle, FaForward } from "react-icons/fa";
import { formatDuration } from "@/lib/utils";
import Image from "next/image";

const faq = [
    {
        question: "How does the Playlist Length Calculator work?",
        answer: "It fetches all videos in a YouTube playlist, extracts their durations using the YouTube Data API, and sums them up to give you the total watch time."
    },
    {
        question: "Does it calculate correctly for deleted or private videos?",
        answer: "Deleted or private videos are skipped by the API, so the calculation only includes watchable videos."
    },
    {
        question: "What is the 1.25x, 1.5x, and 2x speed calculation?",
        answer: "This shows you how much time you save by watching the playlist at faster speeds. Great for estimating study time or binge-watching sessions."
    },
    {
        question: "Why only the first 500 videos?",
        answer: "To ensure fast performance and fair usage, we currently limit calculations to the first 500 videos of a playlist, which covers 99% of playlists."
    },
];

const howTo = [
    "Copy the URL of a YouTube specific playlist (e.g., https://youtube.com/playlist?list=...)",
    "Paste the URL into the input field below",
    "Click 'Calculate Length' to see the breakdown"
];

const seoContent = `Calculate the total duration of any YouTube playlist instantly. 
Perfect for planning your study schedule, binge-watching sessions, or content consumption. 
See how long it takes to watch at 1.25x, 1.5x, and 2x speeds.`;

interface PlaylistResult {
    playlistInfo: {
        title: string;
        thumbnail: string;
        channelTitle: string;
    };
    videoCount: number;
    totalSeconds: number;
    averageSeconds: number;
}

export default function PlaylistLengthCalculator() {
    const [query, setQuery] = useState("");
    const [result, setResult] = useState<PlaylistResult | null>(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleCalculate = async () => {
        setError("");
        setResult(null);

        if (!query.trim()) {
            setError("Please enter a playlist URL");
            return;
        }

        setLoading(true);

        try {
            const response = await fetch("/api/playlist-length", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({ query: query.trim() }),
            });

            const data = await response.json();

            if (!response.ok) {
                setError(data.error || "Failed to calculate playlist length");
                return;
            }

            setResult(data);
        } catch (err) {
            console.error("Calculation error:", err);
            setError("An unexpected error occurred. Please try again.");
        } finally {
            setLoading(false);
        }
    };

    const formatFullDuration = (seconds: number) => {
        const days = Math.floor(seconds / (3600 * 24));
        const hours = Math.floor((seconds % (3600 * 24)) / 3600);
        const minutes = Math.floor((seconds % 3600) / 60);
        const secs = seconds % 60;

        const parts = [];
        if (days > 0) parts.push(`${days} days`);
        if (hours > 0) parts.push(`${hours} hours`);
        if (minutes > 0) parts.push(`${minutes} minutes`);
        if (secs > 0) parts.push(`${secs} seconds`);

        if (parts.length === 0) return "0 seconds";
        return parts.join(", ");
    };

    // Helper to format concise time for speed cards
    const formatConcise = (seconds: number) => {
        const h = Math.floor(seconds / 3600);
        const m = Math.floor((seconds % 3600) / 60);
        const s = Math.floor(seconds % 60); // Ensure integer

        if (h > 0) return `${h}h ${m}m ${s}s`;
        return `${m}m ${s}s`;
    };

    return (
        <ToolPageLayout
            title="YouTube Playlist Length Calculator"
            description="Find out exactly how long a YouTube playlist is. See total duration and time at different playback speeds."
            faq={faq}
            howTo={howTo}
            seoContent={seoContent}
        >
            <div className="space-y-8">
                {/* Input Section */}
                <div className="max-w-2xl mx-auto">
                    <div className="flex flex-col sm:flex-row gap-4">
                        <div className="flex-1">
                            <Input
                                label="Playlist URL"
                                placeholder="https://youtube.com/playlist?list=..."
                                value={query}
                                onChange={(e) => setQuery(e.target.value)}
                                error={error}
                            />
                        </div>
                        <div className="sm:pt-7">
                            <Button onClick={handleCalculate} isLoading={loading} disabled={loading} className="w-full sm:w-auto">
                                {loading ? (
                                    <>
                                        <FaSpinner className="mr-2 animate-spin" />
                                        Calculating...
                                    </>
                                ) : (
                                    <>
                                        <FaListUl className="mr-2" />
                                        Calculate Length
                                    </>
                                )}
                            </Button>
                        </div>
                    </div>
                </div>

                {/* Results Section */}
                {result && !loading && (
                    <div className="animate-fade-in space-y-6">
                        {/* Playlist Info */}
                        <div className="bg-white dark:bg-gray-800 border border-gray-100 dark:border-gray-700 rounded-2xl p-6 shadow-sm">
                            <div className="flex gap-4 items-center">
                                <div className="relative w-24 h-16 sm:w-32 sm:h-20 shrink-0 rounded-lg overflow-hidden bg-gray-200">
                                    {
                                        result.playlistInfo.thumbnail ? (
                                            <Image
                                                src={result.playlistInfo.thumbnail}
                                                alt={result.playlistInfo.title}
                                                fill
                                                className="object-cover"
