import { NextRequest, NextResponse } from "next/server";

/**
 * AI Thumbnail Image Generation API
 * 
 * Uses Replicate API with FLUX Schnell for fast, affordable thumbnail generation.
 * Generates 2 images per request so users can pick the best one.
 * 
 * Configure REPLICATE_API_TOKEN in your .env.local file.
 */

// FLUX Schnell - Fast and affordable (~$0.003/image)
const FLUX_SCHNELL_VERSION = "5599ed30703defd1d160a25a63321b4dec97101d98b4674bcc56e41f62f35637";

export async function POST(req: NextRequest) {
    try {
        const body = await req.json();
        const { prompt, style } = body;

        if (!prompt) {
            return NextResponse.json(
                { error: "Prompt is required" },
                { status: 400 }
            );
        }

        const apiToken = process.env.REPLICATE_API_TOKEN;

        if (!apiToken) {
            console.warn("REPLICATE_API_TOKEN not configured. Returning placeholder.");
            return NextResponse.json({
                success: true,
                images: [
                    `https://placehold.co/1280x720/1a1a2e/ffffff?text=Thumbnail+Option+1`,
                    `https://placehold.co/1280x720/2e1a2e/ffffff?text=Thumbnail+Option+2`
                ],
                message: "Demo mode - Add REPLICATE_API_TOKEN to .env.local for real generation"
            });
        }

        // Build enhanced prompt
        const enhancedPrompt = buildThumbnailPrompt(prompt, style);

        console.log("Generating 2 thumbnails with FLUX Schnell...");

        // Call Replicate API - Generate 2 images
        const startResponse = await fetch("https://api.replicate.com/v1/predictions", {
            method: "POST",
            headers: {
                "Authorization": `Bearer ${apiToken}`,
                "Content-Type": "application/json",
                "Prefer": "wait"
            },
            body: JSON.stringify({
                version: FLUX_SCHNELL_VERSION,
                input: {
                    prompt: enhancedPrompt,
                    go_fast: true,
                    num_outputs: 2,  // Generate 2 images
                    aspect_ratio: "16:9",
                    output_format: "webp",
                    output_quality: 90
                }
            }),
        });

        if (!startResponse.ok) {
            const errorText = await startResponse.text();
            console.error("Replicate API error:", errorText);
            throw new Error(`Replicate API error: ${startResponse.status}`);
        }

        const prediction = await startResponse.json();

        // Check if output is ready
        if (prediction.output && prediction.output.length > 0) {
            return NextResponse.json({
                success: true,
                images: prediction.output,
            });
        }

        // Poll for result if not ready
        if (prediction.status === "starting" || prediction.status === "processing") {
            const result = await pollForResult(prediction.urls.get, apiToken);

            if (result.output && result.output.length > 0) {
                return NextResponse.json({
                    success: true,
                    images: result.output,
                });
            }
        }

        throw new Error("Failed to generate images");

    } catch (error) {
        console.error("Image generation error:", error);
        return NextResponse.json(
            { error: "Failed to generate images. Please try again." },
            { status: 500 }
        );
    }
}

/**
 * Poll for prediction result
 */
async function pollForResult(url: string, apiToken: string, maxAttempts = 60): Promise<any> {
    for (let i = 0; i < maxAttempts; i++) {
        await new Promise(resolve => setTimeout(resolve, 1000));

        const response = await fetch(url, {
            headers: {
                "Authorization": `Bearer ${apiToken}`,
            },
        });

        const result = await response.json();

        if (result.status === "succeeded") {
            return result;
        }

        if (result.status === "failed" || result.status === "canceled") {
            throw new Error(result.error || "Prediction failed");
        }
    }

    throw new Error("Prediction timed out");
}

/**
 * Build enhanced prompt for better thumbnail results
 */
function buildThumbnailPrompt(userPrompt: string, style?: string): string {
    const styleEnhancements: Record<string, string> = {
        gaming: "vibrant gaming aesthetic with RGB neon lighting, dynamic energy, esports quality",
        tech: "clean minimalist Apple-style, professional studio lighting, premium tech look",
        vlog: "warm golden hour lifestyle photography, natural authentic candid feel",
        education: "bright professional clean look, trustworthy educational style",
        entertainment: "cinematic dramatic lighting, Hollywood movie poster quality",
        business: "executive professional portrait, Forbes cover style, corporate elegance",
        kids: "bright colorful cheerful Pixar-style, family-friendly fun",
        cooking: "appetizing food photography, warm delicious Bon Appetit style"
    };

    const styleBoost = style && styleEnhancements[style.toLowerCase()]
        ? `, ${styleEnhancements[style.toLowerCase()]}`
        : ", professional YouTube thumbnail quality";

    // Concise but effective prompt for FLUX Schnell
    return `${userPrompt}${styleBoost}. Ultra high resolution photograph, 8K detail, professional DSLR quality, sharp focus, vibrant saturated colors, high contrast, clean composition with blurred background. Photo-realistic, NOT illustrated. Absolutely NO text, NO words, NO letters, NO watermarks in the image.`;
}

import { Metadata } from "next";
import { notFound } from "next/navigation";
import Link from "next/link";
import AdPlaceholder from "@/components/ui/AdPlaceholder";
import ShareButtons from "@/components/ui/ShareButtons";
import { FaArrowLeft, FaClock, FaUser, FaCalendar, FaArrowRight } from "react-icons/fa";
import { getBlogPostBySlug, getRelatedPosts, getAllBlogPosts } from "@/config/blog";
import { siteConfig } from "@/config/site";
import { getArticleSchema, getBreadcrumbSchema } from "@/lib/seo";

// Generate static params
export function generateStaticParams() {
    const posts = getAllBlogPosts();
    return posts.map((post) => ({ slug: post.slug }));
}

// Generate metadata
export async function generateMetadata({
    params,
}: {
    params: Promise<{ slug: string }>;
}): Promise<Metadata> {
    const { slug } = await params;
    const post = getBlogPostBySlug(slug);

    if (!post) {
        return { title: "Post Not Found" };
    }

    return {
        title: post.title,
        description: post.metaDescription,
        keywords: post.keywords,
        authors: [{ name: post.author }],
        openGraph: {
            title: post.title,
            description: post.metaDescription,
            type: "article",
            publishedTime: post.date,
            authors: [post.author],
        },
        twitter: {
            card: "summary_large_image",
            title: post.title,
            description: post.metaDescription,
        },
    };
}

// Parse markdown-like content to JSX
function parseContent(content: string) {
    const lines = content.split('\n');
    const elements: React.ReactNode[] = [];
    let listItems: string[] = [];
    let listType: 'ul' | 'ol' | null = null;

    const flushList = (key: string) => {
        if (listItems.length > 0) {
            const ListTag = listType === 'ol' ? 'ol' : 'ul';
            elements.push(
                <ListTag key={key} className={`space-y-2 my-4 ${listType === 'ol' ? 'list-decimal' : 'list-disc'} pl-6 text-gray-600 dark:text-gray-300`}>
                    {listItems.map((item, i) => (
                        <li key={i} className="leading-relaxed">{parseInlineMarkdown(item)}</li>
                    ))}
                </ListTag>
            );
            listItems = [];
            listType = null;
        }
    };

    const parseInlineMarkdown = (text: string) => {
        // Handle bold
        const parts = text.split(/(\*\*[^*]+\*\*)/g);
        return parts.map((part, i) => {
            if (part.startsWith('**') && part.endsWith('**')) {
                return <strong key={i} className="font-semibold text-gray-900 dark:text-white">{part.slice(2, -2)}</strong>;
            }
            return part;
        });
    };

    lines.forEach((line, index) => {
        const trimmedLine = line.trim();

        // Empty line - flush list and add spacing
        if (trimmedLine === '') {
            flushList(`list-${index}`);
            return;
        }

        // H2 heading
        if (trimmedLine.startsWith('## ')) {
            flushList(`list-${index}`);
            elements.push(
                <h2 key={index} className="text-2xl font-bold text-gray-900 dark:text-white mt-10 mb-4">
                    {trimmedLine.replace('## ', '')}
                </h2>
            );
            return;
        }

        // H3 heading
        if (trimmedLine.startsWith('### ')) {
            flushList(`list-${index}`);
            elements.push(
                <h3 key={index} className="text-xl font-semibold text-gray-900 dark:text-white mt-8 mb-3">
                    {trimmedLine.replace('### ', '')}
                </h3>
            );
            return;
        }

        // Unordered list item
        if (trimmedLine.startsWith('- ')) {
            listType = 'ul';
            listItems.push(trimmedLine.replace('- ', ''));
            return;
        }

        // Ordered list item
        if (/^\d+\.\s/.test(trimmedLine)) {
            listType = 'ol';
            listItems.push(trimmedLine.replace(/^\d+\.\s/, ''));
            return;
        }

        // Bold paragraph (standalone bold)
        if (trimmedLine.startsWith('**') && trimmedLine.endsWith('**')) {
            flushList(`list-${index}`);
            elements.push(
                <p key={index} className="font-semibold text-gray-900 dark:text-white my-4">
                    {trimmedLine.replace(/\*\*/g, '')}
                </p>
            );
            return;
        }

        // Regular paragraph
        flushList(`list-${index}`);
        elements.push(
            <p key={index} className="text-gray-600 dark:text-gray-300 my-4 leading-relaxed">
                {parseInlineMarkdown(trimmedLine)}
            </p>
        );
    });

    // Flush any remaining list
    flushList('list-final');

    return elements;
}

export default async function BlogPostPage({
    params,
}: {
    params: Promise<{ slug: string }>;
}) {
    const { slug } = await params;
    const post = getBlogPostBySlug(slug);

    if (!post) {
        notFound();
    }

    const relatedPosts = getRelatedPosts(slug, 2);

    // Generate structured data for SEO
    const articleSchema = getArticleSchema({
        title: post.title,
        description: post.metaDescription,
        author: post.author,
        datePublished: post.date,
        url: `${siteConfig.url}/blog/${slug}`,
        keywords: post.keywords,
    });

    const breadcrumbSchema = getBreadcrumbSchema([
        { name: "Home", url: siteConfig.url },
        { name: "Blog", url: `${siteConfig.url}/blog` },
        { name: post.title, url: `${siteConfig.url}/blog/${slug}` },
    ]);

    return (
        <>
            {/* JSON-LD Structured Data */}
            <script
                type="application/ld+json"
                dangerouslySetInnerHTML={{
                    __html: JSON.stringify(articleSchema),
                }}
            />
            <script
                type="application/ld+json"
                dangerouslySetInnerHTML={{
                    __html: JSON.stringify(breadcrumbSchema),
                }}
            />

            <div className="min-h-screen bg-white dark:bg-gray-900">
                {/* Header */}
                <header className="hero-gradient-light dark:hero-gradient py-12 lg:py-16">
                    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                        {/* Back Link */}
                        <Link
                            href="/blog"
                            className="inline-flex items-center gap-2 text-red-500 hover:text-red-600 mb-8 group"
                        >
                            <FaArrowLeft className="group-hover:-translate-x-1 transition-transform" />
                            Back to Blog
                        </Link>

                        {/* Category */}
                        <span className="inline-block px-4 py-1.5 text-sm font-medium bg-red-100 dark:bg-red-900/30 text-red-600 dark:text-red-400 rounded-full mb-4">
                            {post.category}
                        </span>

                        {/* Title */}
                        <h1 className="text-3xl md:text-4xl lg:text-5xl font-bold text-gray-900 dark:text-white mb-6 leading-tight">
                            {post.title}
                        </h1>

                        {/* Meta */}
                        <div className="flex flex-wrap items-center gap-6 text-gray-600 dark:text-gray-400">
                            <div className="flex items-center gap-2">
                                <div className="w-10 h-10 rounded-full bg-gradient-to-br from-red-500 to-orange-500 flex items-center justify-center text-white font-bold">
                                    {post.author.charAt(0)}
                                </div>
                                <div>
                                    <span className="block text-gray-900 dark:text-white font-medium">{post.author}</span>
                                    <span className="text-sm">{post.authorRole}</span>
                                </div>
                            </div>
                            <span className="flex items-center gap-2">
                                <FaCalendar className="w-4 h-4" />
                                {post.date}
                            </span>
                            <span className="flex items-center gap-2">
                                <FaClock className="w-4 h-4" />
                                {post.readTime}
                            </span>
                        </div>
                    </div>
                </header>

                {/* Content */}
                <article className="py-12">
                    <div className="max-w-3xl mx-auto px-4 sm:px-6 lg:px-8">
                        {/* Ad Placeholder */}
                        <div className="mb-10">
                            <AdPlaceholder size="banner" />
                        </div>

                        {/* Article Content */}
                        <div className="prose prose-lg max-w-none">
                            {parseContent(post.content)}
                        </div>

                        {/* Tags */}
                        <div className="mt-12 pt-8 border-t border-gray-200 dark:border-gray-700">
                            <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
                                <div className="flex flex-wrap gap-2">
                                    {post.keywords.map((keyword) => (
                                        <span
                                            key={keyword}
                                            className="px-3 py-1 bg-gray-100 dark:bg-gray-800 text-gray-600 dark:text-gray-400 rounded-full text-sm"
                                        >
                                            {keyword}
                                        </span>
                                    ))}
                                </div>
                                <ShareButtons
                                    url={`${siteConfig.url}/blog/${slug}`}
                                    title={post.title}
                                    description={post.metaDescription}
                                />
                            </div>
                        </div>

                        {/* Ad Placeholder */}
                        <div className="mt-10">
                            <AdPlaceholder size="inline" />
                        </div>
                    </div>
                </article>

                {/* Related Posts */}
                {relatedPosts.length > 0 && (
                    <section className="py-12 bg-gray-50 dark:bg-gray-800/50">
                        <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                            <h2 className="text-2xl font-bold text-gray-900 dark:text-white mb-8">
                                Keep Reading
                            </h2>
                            <div className="grid md:grid-cols-2 gap-6">
                                {relatedPosts.map((relatedPost) => (
                                    <Link key={relatedPost.slug} href={`/blog/${relatedPost.slug}`} className="group">
                                        <article className="h-full bg-white dark:bg-gray-800 rounded-xl p-6 shadow-lg hover:shadow-xl transition-all">
                                            <span className="text-sm text-red-500 font-medium">{relatedPost.category}</span>
                                            <h3 className="text-lg font-bold text-gray-900 dark:text-white mt-2 mb-2 group-hover:text-red-500 transition-colors">
                                                {relatedPost.title}
                                            </h3>
                                            <p className="text-gray-600 dark:text-gray-400 text-sm line-clamp-2">
                                                {relatedPost.excerpt}
                                            </p>
                                        </article>
                                    </Link>
                                ))}
                            </div>
                        </div>
                    </section>
                )}

                {/* CTA */}
                <section className="py-16 bg-white dark:bg-gray-900">
                    <div className="max-w-4xl mx-auto px-4 sm:px-6 lg:px-8">
                        <div className="bg-gradient-to-br from-gray-900 to-gray-800 dark:from-gray-800 dark:to-gray-900 rounded-3xl p-8 md:p-12 text-center">
                            <h2 className="text-2xl md:text-3xl font-bold text-white mb-4">
                                Ready to Level Up Your Channel?
                            </h2>
                            <p className="text-gray-300 mb-8 max-w-xl mx-auto">
                                Put these tips into action with our free YouTube tools. Generate better titles,
                                plan your content, and grow your audience.
                            </p>
                            <Link
                                href="/tools"
                                className="inline-flex items-center gap-2 px-8 py-4 bg-white hover:bg-gray-100 text-gray-900 rounded-xl font-medium transition-colors"
                            >
                                Explore Tools
                                <FaArrowRight />
                            </Link>
                        </div>
                    </div>
                </section>
            </div>
        </>
    );
}

import type { Metadata, Viewport } from "next";
import { Inter } from "next/font/google";
import "./globals.css";
import Header from "@/components/layout/Header";
import Footer from "@/components/layout/Footer";
import ScrollToTop from "@/components/ui/ScrollToTop";
import CookieConsent from "@/components/ui/CookieConsent";
import { siteConfig } from "@/config/site";
import { getOrganizationSchema, getWebsiteSchema } from "@/lib/seo";

const inter = Inter({
  variable: "--font-geist-sans",
  subsets: ["latin"],
  display: "swap",
  preload: true,
});

// Viewport configuration for better mobile performance
export const viewport: Viewport = {
  width: "device-width",
  initialScale: 1,
  maximumScale: 5,
  themeColor: [
    { media: "(prefers-color-scheme: light)", color: "#ffffff" },
    { media: "(prefers-color-scheme: dark)", color: "#0a0a0a" },
  ],
};

export const metadata: Metadata = {
  title: {
    default: siteConfig.seo.defaultTitle,
    template: siteConfig.seo.titleTemplate,
  },
  description: siteConfig.seo.defaultDescription,
  keywords: siteConfig.seo.keywords,
  authors: [{ name: siteConfig.name }],
  creator: siteConfig.name,
  publisher: siteConfig.name,
  metadataBase: new URL(siteConfig.url),
  alternates: {
    canonical: siteConfig.url,
  },
  openGraph: {
    type: "website",
    locale: siteConfig.seo.openGraph.locale,
    siteName: siteConfig.seo.openGraph.siteName,
    title: siteConfig.seo.defaultTitle,
    description: siteConfig.seo.defaultDescription,
    url: siteConfig.url,
    images: [
      {
        url: `${siteConfig.url}/og-image.png`,
        width: 1200,
        height: 630,
        alt: siteConfig.name,
      },
    ],
  },
  twitter: {
    card: "summary_large_image",
    title: siteConfig.seo.defaultTitle,
    description: siteConfig.seo.defaultDescription,
    images: [`${siteConfig.url}/og-image.png`],
    creator: "@youtubetools",
  },
  robots: {
    index: true,
    follow: true,
    nocache: false,
    googleBot: {
      index: true,
      follow: true,
      noimageindex: false,
      "max-video-preview": -1,
      "max-image-preview": "large",
      "max-snippet": -1,
    },
  },
  verification: {
    // ⚠️ IMPORTANT: Replace with your actual Google Search Console verification code
    // To get this:
    // 1. Go to https://search.google.com/search-console
    // 2. Add your property (website URL)
    // 3. Choose "HTML tag" verification method
    // 4. Copy the content value from the meta tag they provide
    google: "YOUR_GOOGLE_VERIFICATION_CODE_HERE",
    // Optional: Bing Webmaster Tools
    // yandex: "YOUR_YANDEX_CODE",
    // other: { "msvalidate.01": "YOUR_BING_CODE" },
  },
  category: "technology",
  classification: "YouTube Tools, SEO Tools, Content Creator Tools",
};

export default function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  // Generate JSON-LD structured data
  const organizationSchema = getOrganizationSchema();
  const websiteSchema = getWebsiteSchema();

  return (
    <html lang="en" suppressHydrationWarning>
      <head>
        {/* DNS Prefetch for external resources */}
        <link rel="dns-prefetch" href="https://fonts.googleapis.com" />
        <link rel="dns-prefetch" href="https://i.ytimg.com" />
        <link rel="dns-prefetch" href="https://www.google-analytics.com" />
        <link rel="preconnect" href="https://fonts.gstatic.com" crossOrigin="anonymous" />

        {/* Favicons */}
        <link rel="icon" href="/favicon.svg" type="image/svg+xml" />
        <link rel="apple-touch-icon" href="/icon.svg" />

        {/* Preload critical resources */}
        <link rel="preload" as="style" href="https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap" />

        {/* JSON-LD Structured Data for SEO */}
        <script
          type="application/ld+json"
          dangerouslySetInnerHTML={{
            __html: JSON.stringify(organizationSchema),
          }}
        />
        <script
          type="application/ld+json"
          dangerouslySetInnerHTML={{
            __html: JSON.stringify(websiteSchema),
          }}
        />

        {/* Theme initialization script - runs before paint to prevent flash */}
        <script
          dangerouslySetInnerHTML={{
            __html: `
              (function() {
                try {
                  var theme = localStorage.getItem('theme');
                  if (theme === 'dark' || (!theme && window.matchMedia('(prefers-color-scheme: dark)').matches)) {
                    document.documentElement.classList.add('dark');
                  } else {
                    document.documentElement.classList.remove('dark');
                  }
                } catch (e) {}
              })();
            `,
          }}
        />

        {/* 
          ⚠️ GOOGLE ANALYTICS - Uncomment and add your GA4 Measurement ID
          Go to: https://analytics.google.com → Admin → Data Streams → Web
        */}
        {/*
        <script async src="https://www.googletagmanager.com/gtag/js?id=G-XXXXXXXXXX"></script>
        <script
          dangerouslySetInnerHTML={{
            __html: `
              window.dataLayer = window.dataLayer || [];
              function gtag(){dataLayer.push(arguments);}
              gtag('js', new Date());
              gtag('config', 'G-XXXXXXXXXX');
            `,
          }}
        />
        */}
      </head>
      <body className={`${inter.variable} antialiased min-h-screen flex flex-col`}>
        <Header />
        <main className="flex-1">{children}</main>
        <Footer />
        <ScrollToTop />
        <CookieConsent />
      </body>
    </html>
  );
}

